$(document).ready(function() {
  
// FitText
// jQuery(".hero-banner .post-title").fitText(1.5, { minFontSize: '32px', maxFontSize: '70px' });
  
//$.get("images/icons/icon-sprite.svg", function(data) {
  //var div = document.createElement("div");
  //div.innerHTML = new XMLSerializer().serializeToString(data.documentElement);
  //document.body.insertBefore(div, document.body.childNodes[0]);
//});

  
  // SVG fallback
  if (!Modernizr.svg) {
  	$('img[src$=".svg"]').attr('src', '.png');
  }
  
  $('.searchBox input[type="text"]').removeAttr('type').attr('type' , 'search');
  $('.searchBox input[type="submit"]').removeAttr('class');
  
  
  
  $('.searchBox').removeClass('searchBox').addClass('search-bar').attr('role' , 'search');
  
  // Flickity PACKAGED v1.1.0 http://flickity.metafizzy.co
  $('.full-width-slider .gallery').flickity({
    // options
  	  lazyLoad: true,
  	});
  	
  	// Content Post Slider
  	$('.content-post-slider .gallery').flickity({
    // options
  	  autoPlay: 8500,
  	  arrowShape: { 
        x0: 10,
        x1: 60, y1: 50,
        x2: 80, y2: 40,
        x3: 45
      },
  	  draggable: false,
  	  selectedAttraction: 0.01,
      friction: 0.15,
      wrapAround: false
  	});
  	
  	// Promo Slider
  	var $gallery = $('.promo-slider .gallery').flickity({
    	//autoPlay: true,
    	contain: true,
    	//prevNextButtons: false,
    	//pageDots: false,
      initialIndex: 0,
      //wrapAround: true
    });
    var $logger = $('.logger');
    
    $gallery.on( 'staticClick', function( event, pointer, cellElement, cellIndex ) {
      // dismiss if cell was not clicked
      if ( !cellElement ) {
        return;
      }
      // change cell background with .is-clicked
      $gallery.find('.is-clicked').removeClass('is-clicked');
      $( cellElement ).addClass('is-clicked');
      $logger.text( 'Cell ' + ( cellIndex + 1 )  + ' clicked' );
    });
    
    // Tab Slider
    var $gallery = $('.tab-slider .gallery').flickity({
      lazyLoad: true,
      pageDots: false,
  	  prevNextButtons: false
    });
  	  $('.button-group').on( 'click', '.btn-txt', function() {
  	    var index = $(this).index();
  	    $gallery.flickity( 'select', index );    
    });
    
    
    $('.tab-slider .gallery-cell').each(function(){
      $(".tab-slider .button-group").append('<a href="javascript:void(0);" class="btn-txt">'+$(this).data('name')+'</a>');
    });
    
    // added Flickity class to make .gallery-cell 100% tall.
    
    //$('.gallery').addClass('flickity-loaded');
    
    
    // Search Value Clear
    //For any clear-focus textbox
    $('input').each(function() {
    var default_value = this.placeholder;
    $(this).focus(function() {
        if (this.placeholder === default_value) {
            this.placeholder = '';
        }
    });
    $(this).blur(function() {
        if (this.placeholder === '') {
            this.placeholder = default_value;
        }
    });
  });
  
   // Show/Hide Panel on calendar list
  $('.show-panel').click(function() {
    $(this).parent().parent().toggleClass('panel-open');     
  });
  
  // Kentico class resets
  
  $('.PagerResults').removeAttr('style');
  $('.PagerControl').toggleClass('PagerControl').addClass('pager-control');
	$('.PagerNumberArea').toggleClass('PagerNumberArea').addClass('pager-number-area');
	$('.PagerResults').toggleClass('PagerResults').addClass('pager-results');
	$('.SelectedPrev').toggleClass('SelectedPrev').addClass('selected-prev');
	$('.SelectedPage').toggleClass('SelectedPage').addClass('selected-page');
	$('.UnselectedPage').toggleClass('UnselectedPage').addClass('unselected-page');
	$('.UnselectedNext').toggleClass('UnselectedNext').addClass('unselected-next');
  

  // Venobox lightbox
  $('.venobox').venobox({
      numeratio: true,            // default: false
      infinigall: true            // default: false
  }); 
  
  // custom lightbox
  $('.venobox_custom').venobox({
      framewidth: '400px',        // default: ''
      frameheight: '300px',       // default: ''
      border: '10px',             // default: '0'
      bgcolor: '#5dff5e',         // default: '#fff'
      titleattr: 'data-title',    // default: 'title'
      numeratio: true,            // default: false
      infinigall: true            // default: false
  });

  // auto-open #firstlink on page load
  //$("#firstlink").venobox().trigger('click');
  
    
});

$(function() {
  $('.site-header a[href*=#]:not([href=#])').click(function() {
    if (location.pathname.replace(/^\//,'') == this.pathname.replace(/^\//,'') && location.hostname == this.hostname) {
      var target = $(this.hash);
      target = target.length ? target : $('[name=' + this.hash.slice(1) +']');
      if (target.length) {
        $('html,body').animate({
          scrollTop: target.offset().top
        }, 1000);
        return false;
      }
    }
  });
});